#!/bin/bash

function make_dark_color()  {
  local COLOR="rgb( $((RANDOM % 50 +48 )), $((RANDOM % 50 + 48)) ,$((RANDOM % 50+48)) )"
  echo $COLOR
}

function make_light_color()  {
  local COLOR="rgb( $((RANDOM % 64 +192 )), $((RANDOM % 64 + 192)) ,$((RANDOM % 64+192)) )"
  echo $COLOR
}

BG1=$(make_dark_color)
BG2=$(make_dark_color)
BG3=$(make_dark_color)

LT1=$(make_light_color)
LT2=$(make_light_color)
LT3=$(make_light_color)




function get_components() {
  echo ""
  echo "---------------------------------"
  echo "1. Installation                  " 
  echo "---------------------------------"

  npm init -y
  npm install @11ty/eleventy              \
              @11ty/eleventy-plugin-rss   \
              @11ty/eleventy-cache-assets \
              sass                \
              get-google-fonts    \
              concurrently        \
              gulp                \
              gulp-clean-css      \
              gulp-sass           \
              gulp-imagemin@7.0.0 \
              moment              \
              node-fetch 
}

function make_dirs() {
  echo ""
  echo "---------------------------------"
  echo "2. Creating directories          " 
  echo "---------------------------------"

  mkdir _site
  mkdir src
  mkdir gulp-tasks
  mkdir src/_data
  mkdir -p src/_includes/layouts
  mkdir -p src/_includes/partials/pagination
  mkdir src/posts
  mkdir -p src/css/blocks
  mkdir src/css/utilities
  mkdir src/scss

}

function make_config() {
  echo ""
echo "---------------------------------"
echo "3. Creating .eleventyjs file     " 
echo "---------------------------------"

cat << EOF > .eleventy.js
const rssPlugin = require('@11ty/eleventy-plugin-rss');

module.exports = config => {

    // don't use .gitignore
    config.setUseGitIgnore(false);

    // copy images and css to dist
    config.addPassthroughCopy('./src/images/');
    config.addPassthroughCopy('./src/css/');

    // create a collection of posts       
    config.addCollection('blog', collection => {
        var c = collection.getFilteredByGlob('./src/posts/*.md');
        c = c.sort( (_a,_b) => {
          return _a.date < _b.date ? 1 : _a.date > _b.date ? -1 : 0;
        });
        return c;
    });

    const collsToExclude =["blog","pages","all","tagsByFrequency","tagsAlphabetical"];

    config.addCollection("tagsByFrequency", collection=> {
      var res = []
      obj = collection.items[0].data.collections;
      for (var prop in obj) {
       if (!collsToExclude.includes(prop))
        {
          res.push({ "tag": prop, "numArticles" : obj[prop].length});
        }
      }
      return res.sort ( (a,b) => 
        a.numArticles < b.numArticles ? 1 : a.numArticles > b.numArticles ? -1 :  0
      );
    });

    config.addCollection("tagsAlphabetical", collection=> {
      var res = []
      obj = collection.items[0].data.collections;
      for (var prop in obj) {
       if (!collsToExclude.includes(prop))
        {
          res.push({ "tag": prop, "numArticles" : obj[prop].length});
        }
      }
      return res.sort ( (a,b) => 
        a.tag < b.tag ? -1 : a.tag > b.tag ? 1 :  0
      );
    });

    // create a collection of ordered pages
    config.addCollection("pages", function(collectionApi) {
        var c = collectionApi.getAll()
            .filter ( f => "type" in f.data  && f.data.type=="page" &&  "order" in f.data)
            .map( c=> {
                c.order = c.data.order;
                return c;
            })
        c= c.sort( (_a,_b) => {
            const a = parseInt(_a.order)
            const b = parseInt(_b.order)
            return a < b ? -1 : a > b ? 1 : 0;
        })
        return c
      });

    config.addPlugin(rssPlugin);

    // everything using nunjucks
    return {
      // remove TemplateEngine directive if you want to use other templating engines
      markdownTemplateEngine: 'njk',
      dataTemplateEngine: 'njk',
      htmlTemplateEngine: 'njk',
      dir: {
        input: 'src',
        output: 'dist'
      }
    };
  };



EOF

cat << EOF > src/posts/posts.json
{
  "layout": "layouts/post.html"
}
EOF

cat << EOF > src/_data/site.json
{
  "name": "Yet Another Eleventy Site",
  "creator" : "11tysetup.sh"
}
EOF


}

function make_gitignore() {
  echo ""
echo "---------------------------------"
echo "4. Creating .gitignore " 
echo "---------------------------------"

cat << EOF > .gitignore
# Misc
*.log
npm-debug.*
*.scssc
*.swp
.DS_Store
Thumbs.db
.sass-cache
.env
.cache

# Node modules and output
node_modules
dist
src/_includes/css
EOF

}

function make_eleventy_ignore() {
  echo ""
echo "---------------------------------"
echo "5. Creating .eleventyignore " 
echo "---------------------------------"

cat << EOF > .eleventyignore
node_modules
EOF
}

function make_templates() {

echo ""
echo "---------------------------------"
echo "6. Creating page templates in    " 
echo "   ./_src/includes/layouts       " 
echo "---------------------------------"

cat << EOF > src/_includes/partials/head.html
<head>
    <title>{{ site.name }} - {{ title }}</title>
    <meta charset="UTF-8" />
    <meta name="description" content="{{ title }} - Bootstrapped with {{ site.creator}}" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <meta http-equiv="X-UA-Compatible" content="ie=edge" />
    <link rel="stylesheet" href="/css/reset.css">
    <link rel="stylesheet" href="/css/sysfonts.css">
    <link rel="stylesheet" href="/css/blocks/header.css">
    <link rel="stylesheet" href="/css/blocks/footer.css">
    <link rel="stylesheet" href="/css/blocks/main.css">
    <link rel="stylesheet" href="/css/utilities/colors.css">
    <link rel="stylesheet" href="/css/utilities/flexutils.css">

    <style>
    </style>
</head>

EOF

cat << EOF > src/_includes/partials/page-menu.html
    <nav class="perfect-center fs2 bc-bg1 c-lt1">
    <ul role="menu">
    {% for item in collections.pages %}
    <!-- {{ item.url }}  {{ page.url }}  {{ item.url == page.url }} -->
    {% if item.url != page.url %}
    <li class="p3" role="menuitem"><a class="c-lt1"  href="{{ item.url }}" aria-label="See {{ item.data.title }}">{{ item.data.title}}</a></li>
    {% else %}
    <li class="p3 bc-lt1" role="menuitem"><a class="c-bg1"  href="{{ item.url }}" aria-label="See {{ item.data.title }}"><b>{{ item.data.title}}</b></a></li>  
    {% endif %}
    {% endfor %}        
    </ul>
    </nav>
EOF

cat << EOF > src/_includes/partials/footer.html
<footer class="footer c-lt2 bc-bg2 perfect-center p4">
    <section class="footer__container">
        <div class="p2">
        <h3>Here, we insert some footer-spanning infos</h3>
        <p>We want our footer to have some footer-spanning &copy; infos, some blurb about us
        and obviously, something about <a class="c-lt1" href="https://11th.dev">Eleventy</a>.
        </div>
        <div class="p2">
        <h4>Tags </h4>
        <ul class="m2" role="menu">
        {% for tag in collections.tagsAlphabetical%}
        <li role="menuitem"><a class="c-lt1" href="/tag/{{ tag.tag }}">{{ tag.tag }}</a> <span class="fs1">({{ tag.numArticles }})</span></li>
        {% endfor %}
        </ul>
        </div>
        <div class="p2">
        There had been recent examples of travelers arriving from the unknown 
        with certain tales which had been stowed away beneath the companion-ladder, 
        had been dragged thence to make room for a chest, 
        and were now entering upon a narrow gorge leading through the chain of hills 
        through which ran the main gorge was composed of a species with which 
        we watched their movements them.
        </div>
        <div class="p2">
            <ul role="menu">
                <li role="menuitem">Item 1</li>
                <li role="menuitem">Item 2</li>
                <li role="menuitem">Item 3</li>
                <li role="menuitem">Item 4</li>
            </ul>
        </div>
</footer>
EOF

