  <article>
        <h1>{{ pageHeaderTitle}}</h1>
        {% if pageHeaderSummary %}
        <div class="page-header__summary">
         {{ pageHeaderSummary }}
        </div>
        {% endif %}

        <div class="post-list">
          {% if postListHeadline %}
          <h2>{{ postListHeadline }}</h2>
          {% endif %}
        <div>
        <ol>
        {% for item in postListItems %}
        <li><a class="c-bg1" href="{{ item.url }}">{{ item.data.title }}</a> - <span class="fs1 bg1">{{ item.data.date }}</span></li>
        {% endfor %}
        </ol>
        </div>

        {# If we leave pagination in for tags, the next and prev links will link to tags and be rather confusing, so don't render in that situation #}
  </article>
{% endblock %}
